import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Axios from 'axios';
import { detailsOrder, deliverOrder } from '../actions/orderActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { ORDER_DELIVER_RESET } from '../constants/orderConstants';

import { deleteOrder, listOrders } from '../actions/orderActions';
export default function ProductEditScreen(props) {
  const order = props.match.params.id;
  const [deliver, setDeliver] = useState('');
  // const orderList = useSelector((state) => state.orderList);

  // const {  order } = orderList;
//   const productDetails = useSelector((state) => state.productDetails);
//   const { loading, error, orderId } = productDetails;

  const orderDeliver = useSelector((state) => state.orderDeliver);
  const {
    loading: loadingDeliver,
    error: errorDeliver,
    success: successDeliver,
  } = orderDeliver;

  const dispatch = useDispatch();
  useEffect(() => {
    if (successDeliver) {
      props.history.push('/orderlist');
    }
    // if (!orderId || successDeliver) {
    //   dispatch({ type: ORDER_DELIVER_RESET });
    // //   dispatch(detailsOrder(orderId));
    // } else {
      setDeliver(order.deliver);
    //   setPrice(product.price);
    //   setImage(product.image);
    //   setCategory(product.category);
    //   setCountInStock(product.countInStock);
    //   setBrand(product.brand);
    //   setDescription(product.description);
    // }
  }, [order, dispatch, , successDeliver, props.history]);
  const submitHandler = (e) => {
    e.preventDefault();
    // TODO: dispatch update product
    dispatch(
        deliverOrder({
        // _id: productId,
        // name,
        // price,
        // image,
        // category,
        // brand,
        // countInStock,
        // description,
        deliver
      })
    );
  };
  // const [loadingDeliver, setLoadingDeliver] = useState(false);
  // const [errorDeliver, setErrorDeliver] = useState('');

//   const userSignin = useSelector((state) => state.userSignin);
//   const { userInfo } = userSignin;
//   const uploadFileHandler = async (e) => {
    // const file = e.target.files[0].name;
    // const bodyFormData = new FormData();
    // bodyFormData.append('image', file);
    // setLoadingUpload(true);
    // try {
    //   const { data } = await Axios.post('/api/uploads', {
        // headers: {
        //   'Content-Type': 'multipart/form-data',
        //   Authorization: `Bearer ${userInfo.token}`,
        // },
    //   });
    //   setImage(data);
    //   setLoadingUpload(false);
    // } catch (error) {
    //   setErrorUpload(error.message);
    //   setLoadingUpload(false);
    // }
//   };

  return (
    <div>
      <form className="form" onSubmit={submitHandler}>
        <div>
          <h1>Hãy nhấn nút nếu bạn đã giao hàng (mã đơn đơn hàng {order.id} )</h1>
        </div>
        {/* {loadingDeliver && <LoadingBox></LoadingBox>} */}
        {/* {errorDeliver && <MessageBox variant="danger">{errorDeliver}</MessageBox>} */}
        {/* {loading ? (
          <LoadingBox></LoadingBox>
        ) : error ? (
          <MessageBox variant="danger">{error}</MessageBox>
        ) : ( */}
          <>
            {/* <div>
              <label htmlFor="name">Name</label>
              <input
                id="name"
                type="text"
                placeholder="Enter name"
                value={name}
                onChange={(e) => setName(e.target.value)}
              ></input>
            </div> */}
            {/* <div>
              <label htmlFor="price">Price</label>
              <input
                id="price"
                type="text"
                placeholder="Enter price"
                value={price}
                onChange={(e) => setPrice(e.target.value)}
              ></input>
            </div> */}
            {/* <div>
              <label htmlFor="image">Image</label>
              <input
                id="image"
                type="text"
                placeholder="Enter image"
                value={ image}
                onChange={(e) => setImage(e.target.value)}
              ></input>
            </div> */}
            {/* <div>
              <label htmlFor="imageFile">Image File</label>
              <input
                type="file"
                id="imageFile"
                label="Choose Image"
                onChange={uploadFileHandler}
              ></input>
              {loadingUpload && <LoadingBox></LoadingBox>}
              {errorUpload && (
                <MessageBox variant="danger">{errorUpload}</MessageBox>
              )}
            </div> */}
            {/* <div>
              <label htmlFor="category">Category</label>
              <input
                id="category"
                type="text"
                placeholder="Enter category"
                value={category}
                onChange={(e) => setCategory(e.target.value)}
              ></input>
            </div>
            <div>
              <label htmlFor="brand">Brand</label>
              <input
                id="brand"
                type="text"
                placeholder="Enter brand"
                value={brand}
                onChange={(e) => setBrand(e.target.value)}
              ></input>
            </div>
            <div>
              <label htmlFor="countInStock">Count In Stock</label>
              <input
                id="countInStock"
                type="text"
                placeholder="Enter countInStock"
                value={countInStock}
                onChange={(e) => setCountInStock(e.target.value)}
              ></input>
            </div> */}
            <div>
              <label htmlFor="description">deliver</label>
              <input
                id="deliver"
                // rows="3"
                type="text"
                placeholder="Enter descripion"
              value={deliver}
                onChange={(e) => setDeliver(e.target.value)}
              ></input>
            </div>
            <div>
              <label></label>
              <button className="primary" type="submit">
                Đã giao
              </button>
            </div>
          </>
        
      </form>
    </div>
  );
}
